import { ProjectorMixin } from '@dojo/widget-core/mixins/Projector';
import Route from '@dojo/routing/Route';
import TodoApp from './widgets/TodoApp';
import router from './routes';

const root = document.querySelector('my-app') || undefined;

const Projector = ProjectorMixin(TodoApp);
const projector = new Projector();

// TODO find a better place for this
const filterRoute = new Route<any, any>({
	path: '/{filter}',

	exec(request) {
		const { filter } = request.params;
		projector.setProperties({ filter });
	}
});
router.append(filterRoute);

projector.append(root);
router.start();

import Router from '@dojo/routing/Router';
import { Parameters } from '@dojo/routing/interfaces';
import HashHistory from '@dojo/routing/history/HashHistory';

interface FilterParameters extends Parameters {
	filter: 'active' | 'all' | 'completed';
}

const router = new Router({ history: new HashHistory() });
export default router;

.filters {
	margin: 0;
	padding: 0;
	list-style: none;
	position: absolute;
	right: 0;
	left: 0;
}

.filters li {
	display: inline;
}

.filters li a {
	color: inherit;
	margin: 3px;
	padding: 3px 7px;
	text-decoration: none;
	border: 1px solid transparent;
	border-radius: 3px;
}

.filters li a:hover {
	border-color: rgba(175, 47, 47, 0.1);
}

.filters li a.selected {
	border-color: rgba(175, 47, 47, 0.2);
}

.todoapp {
	background: #fff;
	margin: 130px 0 40px 0;
	position: relative;
	box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.2),
	            0 25px 50px 0 rgba(0, 0, 0, 0.1);
}

.main {
	position: relative;
	z-index: 2;
	border-top: 1px solid #e6e6e6;
}

export const footer: string;
export const todoCount: string;
export const clearCompleted: string;

.footer {
	color: #777;
	padding: 10px 15px;
	height: 20px;
	text-align: center;
	border-top: 1px solid #e6e6e6;
}

.footer:before {
	content: '';
	position: absolute;
	right: 0;
	bottom: 0;
	left: 0;
	height: 50px;
	overflow: hidden;
	box-shadow: 0 1px 1px rgba(0, 0, 0, 0.2),
	            0 8px 0 -3px #f6f6f6,
	            0 9px 1px -3px rgba(0, 0, 0, 0.2),
	            0 16px 0 -6px #f6f6f6,
	            0 17px 2px -6px rgba(0, 0, 0, 0.2);
}

.todoCount {
	float: left;
	text-align: left;
}

.todoCount strong {
	font-weight: 300;
}

.clearCompleted {
	float: right;
	position: relative;
	line-height: 20px;
	text-decoration: none;
	cursor: pointer;
}

.clearCompleted:hover {
	text-decoration: underline;
}

.title {
	position: absolute;
	top: -155px;
	width: 100%;
	font-size: 100px;
	font-weight: 100;
	text-align: center;
	color: rgba(175, 47, 47, 0.15);
	text-rendering: optimizeLegibility;
}

.newTodo {
	padding: 16px 16px 16px 60px;
	border: none;
	background: rgba(0, 0, 0, 0.003);
	box-shadow: inset 0 -2px 1px rgba(0,0,0,0.03);
	font-style: italic;
	font-weight: 300;
	color: #e6e6e6;
	position: relative;
	margin: 0;
	width: 100%;
	font-size: 24px;
	font-family: inherit;
	font-weight: inherit;
	line-height: 1.4em;
	color: inherit;
	box-sizing: border-box;
	-webkit-font-smoothing: antialiased;
	-moz-osx-font-smoothing: grayscale;
}

.toggleAll {
	position: absolute;
	top: 10px;
	left: -12px;
	width: 60px;
	height: 34px;
	text-align: center;
	border: none;
}

.toggleAll:before {
	content: '';
	font-size: 22px;
	color: #e6e6e6;
	padding: 10px 27px 10px 27px;
}

.toggleAll:checked:before {
	color: #737373;
}

@media screen and (-webkit-min-device-pixel-ratio:0) {
	.toggleAll {
		-webkit-transform: rotate(90deg);
		transform: rotate(90deg);
		-webkit-appearance: none;
		appearance: none;
		background: none;
	}
}

export const title: string;
export const newTodo: string;
export const toggleAll: string;

.todoItem {
	position: relative;
	font-size: 24px;
	border-bottom: 1px solid #ededed;
}

.todoItem:last-child {
	border-bottom: none;
}

.edit {
	position: relative;
	margin: 0;
	width: 100%;
	font-size: 24px;
	font-family: inherit;
	font-weight: inherit;
	line-height: 1.4em;
	border: 0;
	color: inherit;
	padding: 6px;
	border: 1px solid #999;
	box-shadow: inset 0 -1px 5px 0 rgba(0, 0, 0, 0.2);
	box-sizing: border-box;
	-webkit-font-smoothing: antialiased;
	-moz-osx-font-smoothing: grayscale;
}

.editing {
	border-bottom: none;
	padding: 0;
}

.editing .edit {
	display: block;
	width: 506px;
	padding: 12px 16px;
	margin: 0 0 0 43px;
}

.editing .view {
	display: none;
}

.toggle {
	text-align: center;
	width: 40px;
	height: auto;
	position: absolute;
	top: 0;
	bottom: 0;
	margin: auto 0;
	border: none;
	appearance: none;
}

.toggle:after {
	content: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" width="40" height="40" viewBox="-10 -18 100 135"><circle cx="50" cy="50" r="50" fill="none" stroke="#ededed" stroke-width="3"/></svg>');
}

.toggle:checked:after {
	content: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" width="40" height="40" viewBox="-10 -18 100 135"><circle cx="50" cy="50" r="50" fill="none" stroke="#bddad5" stroke-width="3"/><path fill="#5dc2af" d="M72 25L42 71 27 56l-4 4 20 20 34-52z"/></svg>');
}

@media screen and (-webkit-min-device-pixel-ratio:0) {
	.toggle {
		height: 40px;
		background: none;
	}
}

.todoLabel {
	word-break: break-all;
	padding: 15px 60px 15px 15px;
	margin-left: 45px;
	display: block;
	line-height: 1.2;
	transition: color 0.4s;
}

.completed {
	color: #d9d9d9;
	text-decoration: line-through;
}

.destroy {
	display: none;
	position: absolute;
	top: 0;
	right: 10px;
	bottom: 0;
	width: 40px;
	height: 40px;
	margin: auto 0;
	font-size: 30px;
	color: #cc9a9a;
	margin-bottom: 11px;
	transition: color 0.2s ease-out;
}

.destroy:hover {
	color: #af5b5e;
}

.destroy:after {
	content: '';
}

.todoItem:hover .destroy {
	display: block;
}

.edit {
	display: none;
}

.editing:last-child {
	margin-bottom: -1px;
}

.todoList {
	margin: 0;
	padding: 0;
	list-style: none;
	display: block;
}

.todoList li {
	position: relative;
	font-size: 24px;
	border-bottom: 1px solid #ededed;
}

