import { WidgetBase } from '@dojo/widget-core/WidgetBase';
import { WidgetProperties } from '@dojo/widget-core/interfaces';
import { ThemeableMixin, theme } from '@dojo/widget-core/mixins/Themeable';
import { w, v } from '@dojo/widget-core/d';
import { registry } from '@dojo/widget-core/d';

import TodoHeader from './TodoHeader';
import TodoList from './TodoList';
import TodoItem from './TodoItem';
import TodoFooter from './TodoFooter';
import TodoFilter from './TodoFilter';

import * as css from './styles/todoApp.css';

registry.define('todo-header', TodoHeader);
registry.define('todo-list', TodoList);
registry.define('todo-item', TodoItem);
registry.define('todo-footer', TodoFooter);
registry.define('todo-filter', TodoFilter);

export interface Todo {
	id: string;
	label: string;
	completed: boolean;
	editing?: boolean;
}

export interface TodoAppProperties extends WidgetProperties {
	filter?: string;
}

export const TodoAppBase = ThemeableMixin(WidgetBase);

@theme(css)
export default class TodoApp extends TodoAppBase<TodoAppProperties> {

	private todos: Map<string, Todo> = new Map<string, Todo>();
	private todoItem = '';
	private completedCount = 0;
	private updated: string = uuid();

	render() {
		const { todoItem, updateTodo, updated, todos, completedCount, clearCompleted, editTodo, removeTodo, toggleTodo, toggleAllTodos } = this;
		const { filter: activeFilter = 'all' } = this.properties;
		const allCompleted = todos.size !== 0 && completedCount === todos.size;
		const activeCount = todos.size - completedCount;
		const completedItems = completedCount > 0;

		return v('section', { classes: this.classes(css.todoapp) }, [
			w('todo-header', { value: todoItem, updateTodo, allCompleted, addTodo: this.setTodo, toggleAllTodos }),
			v('section', {}, [
				w('todo-list', { updated, activeFilter, todos, editTodo, removeTodo, toggleTodo, updateTodo: this.setTodo })
			]),
			todos.size ? w('todo-footer', { activeFilter, clearCompleted, activeCount, completedItems }) : null
		]);
	}

	private removeTodo(id: string) {
		const todo = this.todos.get(id);
		if (todo) {
			this.todos.delete(id);
			todo.completed && --this.completedCount;
			this.onUpdate();
		}
	}

	private toggleTodo(id: string) {
		const todo = this.todos.get(id);
		if (todo) {
			const completed = !todo.completed;
			completed ? ++this.completedCount : --this.completedCount;
			this.setTodo({ completed }, id);
		}
	}

	private toggleAllTodos() {
		const completed = this.completedCount !== this.todos.size;
		this.todos.forEach((todo, key) => {
			this.setTodo({ completed }, key);
		});
		this.completedCount = completed ? this.todos.size : 0;
		this.onUpdate();
	}

	private editTodo(id: string) {
		const todo = this.todos.get(id);
		if (todo) {
			this.setTodo({ editing: true}, id);
			this.onUpdate();
		}
	}

	private clearCompleted() {
		this.todos.forEach((todo, key) => {
			if (todo.completed) {
				this.todos.delete(key);
			}
		});
		this.completedCount = 0;
		this.onUpdate();
	}

	private updateTodo(todo: string) {
		this.todoItem = todo;
		this.invalidate();
	}

	private setTodo(todo: Partial<Todo>, id?: string) {
		if (!id) {
			id = uuid();
			this.todoItem = '';
		}
		if (todo.label) {
			todo.label = todo.label.trim();
			if (!todo.label) {
				this.removeTodo(id);
				return;
			}
		}
		this.todos.set(id, assign(<any> { id }, this.todos.get(id) || {}, todo));
		this.onUpdate();
	}

	private onUpdate() {
		this.updated = uuid();
		this.invalidate();
	}
}

import { WidgetBase } from '@dojo/widget-core/WidgetBase';
import { WidgetProperties } from '@dojo/widget-core/interfaces';
import { ThemeableMixin, theme } from '@dojo/widget-core/mixins/Themeable';
import { v } from '@dojo/widget-core/d';

import * as css from './styles/todoHeader.css';

export interface TodoHeaderProperties extends WidgetProperties {
	allCompleted: boolean;
	addTodo: Function;
	toggleAllTodos: Function;
	updateTodo: Function;
	value: string;
}

export const TodoHeaderBase = ThemeableMixin(WidgetBase);

@theme(css)
export default class TodoHeader extends TodoHeaderBase<TodoHeaderProperties> {

	render() {
		const { properties: { value, allCompleted } } = this;
		const newTodoProperties: any = {
			id: 'new-todo',
			afterCreate: this.afterCreate,
			classes: this.classes(css.newTodo),
			onkeyup: this.addTodo,
			oninput: this.updateTodo,
			value,
			placeholder: 'What needs to be done?'
		};

		return v('header', [
			v('h1', { classes: this.classes(css.title) }, [ 'todos' ]),
			v('input', newTodoProperties),
			v('input', { onchange: this.toggleAllTodos, checked: allCompleted, type: 'checkbox', classes: this.classes(css.toggleAll) })
		]);
	}

	private addTodo({ which, target: { value: label } }: any) {
		if (which === 13 && label) {
			this.properties.addTodo({ label, completed: false });
		}
	}

	private updateTodo({ target: { value } }: any) {
		this.properties.updateTodo(value);
	}

	private toggleAllTodos() {
		this.properties.toggleAllTodos();
	}

	private afterCreate(element: HTMLInputElement) {
		setTimeout(() => element.focus(), 0);
	}
}

import { WidgetBase } from '@dojo/widget-core/WidgetBase';
import { WidgetProperties } from '@dojo/widget-core/interfaces';
import { ThemeableMixin, theme } from '@dojo/widget-core/mixins/Themeable';
import { v, w } from '@dojo/widget-core/d';

import * as css from './styles/todoFooter.css';

export interface TodoFooterProperties extends WidgetProperties {
	activeCount: number;
	clearCompleted: Function;
	activeFilter: string;
	completedItems: boolean;
}

export const TodoHeaderBase = ThemeableMixin(WidgetBase);

@theme(css)
export default class TodoFooter extends TodoHeaderBase<TodoFooterProperties> {

	clearCompleted() {
		this.properties.clearCompleted();
	}

	render() {
		const { activeFilter, activeCount, completedItems } = this.properties;
		const countLabel = activeCount === 1 ? 'item' : 'items';

		return v('footer', { classes: this.classes(css.footer) }, [
			v('span', { classes: this.classes(css.todoCount) }, [
				v('strong', [activeCount + ' ']),
				v('span', [countLabel + ' left'])
			]),
			w('todo-filter', { activeFilter }),
			completedItems ? v('button', {
				onclick: this.clearCompleted,
				innerHTML: 'Clear completed',
				classes: this.classes(css.clearCompleted)
			}) : null
		]);
	}
}

import { WidgetBase } from '@dojo/widget-core/WidgetBase';
import { WidgetProperties } from '@dojo/widget-core/interfaces';
import { ThemeableMixin, theme } from '@dojo/widget-core/mixins/Themeable';
import { v } from '@dojo/widget-core/d';
import { Todo } from './TodoApp';

import * as css from './styles/todoItem.css';

export interface TodoItemProperties extends WidgetProperties {
	todo: Todo;
	editing: boolean;
	editTodo: Function;
	toggleTodo: Function;
	removeTodo: Function;
	updateTodo: Function;
}

export const TodoItemBase = ThemeableMixin(WidgetBase);

@theme(css)
export default class TodoItem extends TodoItemBase<TodoItemProperties> {

	render() {
		const { properties: { todo } } = this;

		return v('li', { id: 'todo-item', classes: this.classes(css.todoItem, Boolean(todo.editing) ? css.editing : null, Boolean(todo.completed && !todo.editing) ? css.completed : null) }, [
			v('div', { classes: this.classes(css.view) }, [
				v('input', { id: 'toggle', classes: this.classes(css.toggle), type: 'checkbox', checked: todo.completed, onchange: this.toggleTodo }),
				v('label', { classes: this.classes(css.todoLabel), innerHTML: todo.label, ondblclick: this.editTodo }),
				v('button', { id: 'destroy', onclick: this.removeTodo, classes: this.classes(css.destroy) })
			]),
			todo.editing ? v('input', { afterCreate: this.afterCreate, onkeyup: this.updateTodo, onblur: this.updateTodo, value: todo.label, classes: this.classes(css.edit) }) : null
		]);
	}

	private toggleTodo() {
		this.properties.toggleTodo(this.properties.key);
	}

	private editTodo() {
		this.properties.editTodo(this.properties.key);
	}

	private updateTodo({ which, target: { value: label } }: any) {
		const { properties: { todo, key } } = this;
		const editing = false;

		if (which === 13 || (!which && todo.editing)) {
			label ? this.properties.updateTodo({ label, editing }, key) : this.removeTodo();
		}
		else if (which === 27) {
			this.properties.updateTodo({ editing }, key);
		}
	}

	private removeTodo() {
		this.properties.removeTodo(this.properties.key);
	}

	private afterCreate(element: HTMLInputElement) {
		setTimeout(() => element.focus(), 0);
	}
}

import Map from '@dojo/shim/Map';
import { from as arrayFrom } from '@dojo/shim/array';
import { WidgetBase } from '@dojo/widget-core/WidgetBase';
import { ThemeableMixin, theme } from '@dojo/widget-core/mixins/Themeable';
import { WidgetProperties } from '@dojo/widget-core/interfaces';
import { v, w } from '@dojo/widget-core/d';
import { Todo } from './TodoApp';

import * as css from './styles/todoList.css';

export interface TodoListProperties extends WidgetProperties {
	todos: Map<string, Todo>;
	activeFilter: 'all' | 'active' | 'completed';
	toggleTodo: Function;
	removeTodo: Function;
	editTodo: Function;
	updateTodo: Function;
}

export const TodoListBase = ThemeableMixin(WidgetBase);

function filter(filterName: string, todo: Todo): boolean {
	switch (filterName) {
		case 'completed':
			return !!todo.completed;
		case 'active':
			return !todo.completed;
		default:
			return true;
	}
}

@theme(css)
export default class TodoList extends TodoListBase<TodoListProperties> {
	render() {
		const { properties: { activeFilter, todos, toggleTodo, editTodo, updateTodo, removeTodo } } = this;
		const todoItems = arrayFrom(todos.entries()).filter(([ , value ]) => filter(activeFilter, value));

		return v('ul', { id: 'todo-list', classes: this.classes(css.todoList) }, todoItems.map(([ key, todo ]) => {
			return w('todo-item', { key, todo, toggleTodo, editTodo, removeTodo, updateTodo });
		}));
	}
}

import * as test from 'intern/lib/interfaces/bdd';
import { assert } from 'chai';
import Page from './Page';

test.describe('TodoMVC - Dojo 2', function (this: any) {

	let page: any;

	const TODO_ITEM_ONE = 'buy some cheese';
	const TODO_ITEM_TWO = 'feed the cat';
	const TODO_ITEM_THREE = 'book a doctors appointment';

	test.beforeEach(() => {
		page = new Page(this.remote);
		return page.init();
	});

	test.describe('When page is initially opened', function () {

		test.it('should focus on the todo input field', function (this: any) {
			return page.isNewItemInputFocused()
				.then((isNewItemInputFocused: boolean) => {
					assert.isTrue(isNewItemInputFocused);
				});
		});

	});

	test.describe('No Todos', function () {

		test.it('should hide #main and #footer', function (this: any) {
			return Promise.all([ page.isMainVisible(), page.isFooterVisible() ])
				.then(([ isMainVisible, isFooterVisible ]) => {
					assert.isFalse(isMainVisible);
					assert.isFalse(isFooterVisible);
				});
		});

	});

	test.describe('New Todo', function () {

		test.it('should allow me to add todo items', function () {
			return page.enterItem(TODO_ITEM_ONE)
				.then(() => page.getItem(0))
				.then((itemText: string) => {
					assert.equal(itemText, TODO_ITEM_ONE);
				})
				.then(() => page.enterItem(TODO_ITEM_TWO))
				.then(() => page.getItem(1))
				.then((itemText: string) => {
					assert.equal(itemText, TODO_ITEM_TWO);
				});
		});

		test.it('should clear text input field when an item is added', function () {
			return page.enterItem(TODO_ITEM_ONE)
				.then(() => page.isNewItemInputEmpty())
				.then((isNewItemInputEmpty: boolean) => {
					assert.isTrue(isNewItemInputEmpty);
				});
		});

		test.it('should append new items to the bottom of the list', function () {
			return page.enterItems([ TODO_ITEM_ONE, TODO_ITEM_TWO, TODO_ITEM_THREE ])
				.then(() => Promise.all([ page.getItem(0), page.getItem(1), page.getItem(2) ])
				.then(([ item1, item2, item3 ]) => {
					assert.equal(item1, TODO_ITEM_ONE);
					assert.equal(item2, TODO_ITEM_TWO);
					assert.equal(item3, TODO_ITEM_THREE);
				}));
		});

		test.it('should trim text input', function (this: any) {
			return page.enterItem(`   ${TODO_ITEM_ONE}  `)
				.then(() => page.getItem(0))
				.then((itemText: string) => {
					assert.equal(itemText, TODO_ITEM_ONE);
				});
		});

		test.it('should show #main and #footer when items added', function () {
			return page.enterItem(TODO_ITEM_ONE)
				.then(() => Promise.all([
					page.isMainVisible(),
					page.isFooterVisible()
				]))
				.then(([ isMainVisible, isFooterVisible ]: boolean[]) => {
					assert.isTrue(isMainVisible);
					assert.isTrue(isFooterVisible);
				});
		});

	});

	test.describe('Mark all as completed', function () {

		test.beforeEach(() => {
			return page.enterItems([ TODO_ITEM_ONE, TODO_ITEM_TWO, TODO_ITEM_THREE ]);
		});

		test.it('should allow me to mark all items as completed', function () {
			return page.toggleAll()
				.then(() => page.getCompletedCount())
				.then((completed: number) => {
					assert.equal(completed, 3);
				});
		});

		test.it('should correctly update the complete all checked state', function () {
			return page.toggleItem(0)
				.then(() => page.toggleItem(1))
				.then(() => page.toggleItem(2))
				.then(() => page.isCompleteAllChecked())
				.then((isCompleteAllChecked: boolean) => {
					assert.isTrue(isCompleteAllChecked);
				});
		});

		test.it('should allow me to clear the completion state of all items', function () {
			return page.toggleAll()
				.then(() => page.toggleAll())
				.then(() => page.getCompletedCount())
				.then((completed: number) => {
					assert.strictEqual(completed, 0);
				});
		});

		test.it('complete all checkbox should update state when items are completed / cleared', function () {
			return page.toggleAll()
				.then(() => page.isCompleteAllChecked())
				.then((isCompleteAllChecked: boolean) => {
					assert.isTrue(isCompleteAllChecked);
				})
				.then(() => page.toggleItem(1))
				.then(() => page.isCompleteAllChecked())
				.then((isCompleteAllChecked: boolean) => {
					assert.isFalse(isCompleteAllChecked);
				})
				.then(() => page.toggleItem(1))
				.then(() => page.isCompleteAllChecked())
				.then((isCompleteAllChecked: boolean) => {
					assert.isTrue(isCompleteAllChecked);
				});
		});

	});

	test.describe('Item', function () {

		test.it('should allow me to mark items as complete', function () {
			return page.enterItem(TODO_ITEM_ONE)
				.then(() => page.enterItem(TODO_ITEM_TWO))
				.then(() => page.toggleItem(0))
				.then(() => page.getCompletedCount())
				.then((completed: number) => {
					assert.strictEqual(completed, 1);
				})
				.then(() => page.toggleItem(1))
				.then(() => page.getCompletedCount())
				.then((completed: number) => {
					assert.strictEqual(completed, 2);
				});
		});

		test.it('should allow me to un-mark items as complete', function () {
			return page.enterItem(TODO_ITEM_ONE)
				.then(() => page.enterItem(TODO_ITEM_TWO))
				.then(() => page.toggleItem(0))
				.then(() => page.getCompletedCount())
				.then((completed: number) => {
					assert.strictEqual(completed, 1);
				})
				.then(() => page.toggleItem(0))
				.then(() => page.getCompletedCount())
				.then((completed: number) => {
					assert.strictEqual(completed, 0);
				});
